    header {
        position: fixed;
        height: 50px;
    }

    header ul {
        display: none;
    }

    header .box-icons {
        display: none;
    }

    .menu-icon {
        display: inline;
    }

    .blackhole-box video {
        width: 100%;
        margin-top: -15%;
    }

    .autoBlur {
        animation: none;
    }
    
    .hero {
        flex-direction: column;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
    }

    .hero-info {
        bottom: 5%;
    }

    .scroll-down {
        bottom: 5%;
    }

    .hero .skills-video-box {
        height: 200px;
        top: 5%;
    }

    .card video {
        width: 100%;
    }
    
    .container {
        height: 100%;
    }

    .project-vidbox video {
        width: 250px;
        margin-left: -100px;
    }

    .project-info {
        overflow: hidden;
        padding-left: 0;
        margin-left: -50px;
    }

    .project-info h1 {
        font-size: 20px;
        max-width: 200px;
        text-wrap: wrap;
    }

    .project-info p {
        font-size: 10px;
        text-wrap: wrap;
        max-width: 200px;
        min-width: 0;
    }

    .project-info button {
        padding: 5px 10px;
    }

    .my-project {
        margin-bottom: 600px;
    }

    .project-card {
        flex-direction: column;
        margin-left: 25%;
        gap: 30px;
    }
    
    .project-vidbox {
        min-width: 200px;
    }

    .project-info {
        width: 85%;
    }

    .project-info h1 {
        text-wrap: nowrap;
    }

    .project-info p {
        max-width: 300px;
    }

    .Designer {
        top: 15%;
        left: 18%;
    }

    .Designer h1 {
        margin-bottom: 0;
        margin-top: 70px;
    }

    .coder {
        top: 50%;
        left: 18%;
    }

    .coder h1 {
        margin-bottom: 0;
    }

    .slider .list .item img {
        width: 70%;
    }

    .contact-section {
        flex-direction: column;
        margin-top: 100px;
        margin-bottom: 50px;
    }
    .footer {
        flex-direction: column;
        height: auto;
        padding: 10px;
        text-align: center;
        gap: 8px;
    }

    .footer-text {
        flex: unset;
    }

    .footer .box-icons {
        justify-content: center;
    }
}


@media screen and (max-width: 480px) {
    .hero .skills-video-box {
        display: none;
    }

    .blackhole-box {
        overflow: hidden;
    }

    .blackhole-box video {
        width: 140%;
        margin-top: -27%;
    }

    .left {
        scale: 0.9;
        margin-left: -30px;
    }

    .left h2 {
        font-size: 20px;
    }

    .hero {
        scale: 0.9;
    }

    .hero-info {
        bottom: 15%;
    }

    .hero-info h1 {
        font-size: 35px;
    }

    .scroll-down {
        bottom: 15%;
        left: 60%;
    }

    .section-title {
        font-size: 25px;
    }

    .info-cards {
        display: flex;
        flex-direction: column;
    }

    .card {
        min-height: auto;
    }

    .card h1 {
        bottom: auto;
        font-size: 20px;
    }

    .project-card {
        height: 50%;
    }

    .project-info p {
        margin-bottom: 15px;
    }

    .project-info {
        margin-left: -80px;
    }

    .skills-box {
        height: 120vh;
        margin-right: 30px;
    }

    .slider {
        bottom: 0%;
    }

    .contact-section {
        scale: 0.9;
        height: 120vh;
    }

    .contact-section .section-title {
        top: -30px;
        left: 25%;
    }
    .footer {
        flex-direction: column;
        height: auto;
        padding: 10px;
        text-align: center;
        gap: 8px;
    }

    .footer-text {
        flex: unset;
    }

    .footer .box-icons {
        justify-content: center;
    }
}

@media (max-width: 768px) {
    .timeline::before {
        left: 30px;
    }
    
    .timeline-item {
        width: 100%;
        padding-left: 70px;
        padding-right: 25px;
    }
    
    .timeline-item:nth-child(even) {
        left: 0;
    }
    
    .hero-buttons {
        flex-direction: column;
        gap: 10px;
    }
}

/* =======================
   EXTRA MOBILE FIXES
   ======================= */

/* For tablets and small laptops */
@media screen and (max-width: 992px) {
  .hero-info h1 {
    font-size: 32px;
    line-height: 1.2;
  }
  .hero-info p {
    font-size: 16px;
  }
  .project-card {
    flex-direction: column;
    gap: 20px;
  }
  .project-info {
    padding-left: 0;
    text-align: center;
    align-items: center;
  }
}

/* For mobile phones */
@media screen and (max-width: 600px) {
  body {
    font-size: 14px;
  }

  /* Navbar & header */
  header {
    height: 55px;
    padding: 0 15px;
  }
  .left img {
    width: 70px;
  }

  /* Hero section */
  .hero {
    flex-direction: column;
    justify-content: center;
    text-align: center;
    padding: 20px;
    height: auto;
  }
  .hero-info {
    position: relative;
    left: 0;
    bottom: 0;
    margin: 20px auto;
  }
  .hero-info h1 {
    font-size: 26px;
    line-height: 1.3;
  }
  .hero-info p {
    font-size: 14px;
  }
  .hero-info button {
    padding: 10px 20px;
    font-size: 14px;
  }

  /* Info cards */
  .info-cards {
    grid-template-columns: 1fr;
    gap: 15px;
  }
  .card h1 {
    font-size: 18px;
  }
  .card p {
    font-size: 14px;
  }

  /* Projects */
  .project-card {
    flex-direction: column;
    text-align: center;
  }
  .project-vidbox {
    min-width: unset;
    width: 100%;
  }
  .project-info {
    width: 100%;
    padding: 0;
    margin: 0;
  }

  /* Skills */
  .Designer,
  .coder {
    position: relative;
    top: unset;
    left: unset;
    right: unset;
    margin: 20px auto;
    text-align: center;
  }
  .Designer h1,
  .coder h1 {
    font-size: 28px;
  }

  /* Contact section */
  .contact-section {
    flex-direction: column;
    gap: 30px;
    padding: 20px;
  }
  .contact-section h1 {
    position: relative;
    top: unset;
    left: unset;
    text-align: center;
    margin-bottom: 20px;
  }
  .contact-box input {
    width: 100%;
  }

  /* Footer */
  .footer {
    flex-direction: column;
    gap: 10px;
    height: auto;
    padding: 15px;
    text-align: center;
  }
}
